UPDATE `creature_ai_texts` SET `entry` = -378,`content_default` = '狂暴姿态!不顾一切的攻击他们!',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '23049' WHERE `creature_ai_texts`.`entry` = -378;
UPDATE `creature_ai_texts` SET `entry` = -379,`content_default` = '我的!你不能占据这个地方!',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '24960' WHERE `creature_ai_texts`.`entry` = -379;
UPDATE `creature_ai_texts` SET `entry` = -380,`content_default` = '这不是给你的!滚出这里!',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '24966' WHERE `creature_ai_texts`.`entry` = -380;
UPDATE `creature_ai_texts` SET `entry` = -381,`content_default` = '注意!',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -381;
UPDATE `creature_ai_texts` SET `entry` = -382,`content_default` = '好了，安静下来。我们有很多的事要做，但时间很少，孩子们。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -382;
UPDATE `creature_ai_texts` SET `entry` = -383,`content_default` = '我曾经和某些德莱尼在司凯堤斯与影月谷并肩。但现在我们身在艾泽拉斯，而情势看来更加险恶。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -383;
UPDATE `creature_ai_texts` SET `entry` = -384,`content_default` = '首先，你们这些辛多雷听着。我指挥一艘严格的船舰…这可跟你们习惯的占卜者船舰不同。要么全力以赴，不然就给我滚回去!',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -384;
UPDATE `creature_ai_texts` SET `entry` = -385,`content_default` = '现在快滚过来吧，欢迎来到奎尔达纳斯之岛。太阳之井就在南方的高地上，而凯尔萨斯王子所占据的博学者殿堂则在东边。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -385;
UPDATE `creature_ai_texts` SET `entry` = -386,`content_default` = '我们是来这边协助那些厚颜自称『冒险者』的家伙们。我们会帮他们守住岛上的关键地点，并且让他们远离冲突，好让他们可以正常工作。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -386;
UPDATE `creature_ai_texts` SET `entry` = -387,`content_default` = '如果女士们没有任何问题，你们将可以从小组长那边取得进一步的指示与协助。以上。祝好运!',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -387;
UPDATE `creature_ai_texts` SET `entry` = -388,`content_default` = '致命击杀!',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 5801,`type` = 1,`language` = 0,`emote` = 0,`comment` = '4831' WHERE `creature_ai_texts`.`entry` = -388;
UPDATE `creature_ai_texts` SET `entry` = -389,`content_default` = '欢迎来到太阳之井。尽管温度适中且风景优美，不过各位先生小姐们，你们可不是来渡假的!',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -389;
UPDATE `creature_ai_texts` SET `entry` = -390,`content_default` = '目前的情况看来是:我们尽力固守，而那些较有经验和装备较好的人则去对付凯尔萨斯王子。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -390;
UPDATE `creature_ai_texts` SET `entry` = -391,`content_default` = '我知道，我们也认为他死了，但这不是关键。他试图利用太阳之井将燃烧军团的主人，基尔加丹，带入这个世界。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -391;
UPDATE `creature_ai_texts` SET `entry` = -392,`content_default` = '让我向你们保证，如果他真的成功，这个世界将会变得比外域更悲惨!让我们确保这件事情不会实现。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -392;
UPDATE `creature_ai_texts` SET `entry` = -393,`content_default` = '战士会部属来防守关键位置，你的射手则要协助阻止对方的飞行部队靠近。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -393;
UPDATE `creature_ai_texts` SET `entry` = -394,`content_default` = '我等着你的指派。并且待在这里，做好准备等待关键的一刻!',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -394;
UPDATE `creature_ai_texts` SET `entry` = -395,`content_default` = '你好，新兵。我是训练中士巴度恩。接下来几天你将会接受一连串的适应与训练。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -395;
UPDATE `creature_ai_texts` SET `entry` = -396,`content_default` = '奎尔达纳斯之岛充斥着凯尔萨斯·逐日者王子的部队。情报先前认定他已经死了，但情报单位错了。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -396;
UPDATE `creature_ai_texts` SET `entry` = -397,`content_default` = '在高地之上，他和他的恶魔夥伴正打算要重新点燃太阳之井并且打开一道裂隙让他们的老大基尔加丹进入这个世界。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -397;
UPDATE `creature_ai_texts` SET `entry` = -398,`content_default` = '我们的职责是尽可能的为别人当炮灰，同时支持那些政治家。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -398;
UPDATE `creature_ai_texts` SET `entry` = -399,`content_default` = '不用说，我们其中很多人恐怕再也回不了家。但如果你牢记你的训练，并且随时提高警觉，你就可以大大提高自己的存活机率。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -399;
UPDATE `creature_ai_texts` SET `entry` = -400,`content_default` = '我现在只能说。集中注意力，孩子们。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25162' WHERE `creature_ai_texts`.`entry` = -400;
UPDATE `creature_ai_texts` SET `entry` = -401,`content_default` = '该死的破碎之日人渣!自由开火!',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 1,`language` = 0,`emote` = 0,`comment` = '25132' WHERE `creature_ai_texts`.`entry` = -401;
UPDATE `creature_ai_texts` SET `entry` = -402,`content_default` = '瞄准那些恶魔。我们今天不是来消灭天谴军团的。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 1,`language` = 0,`emote` = 0,`comment` = '25144' WHERE `creature_ai_texts`.`entry` = -402;
UPDATE `creature_ai_texts` SET `entry` = -403,`content_default` = '龙鹰从西边过来了!把那个$C给射下来!',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '25144' WHERE `creature_ai_texts`.`entry` = -403;
UPDATE `creature_ai_texts` SET `entry` = -404,`content_default` = '我不允许你进入神庙!我会摧毁你的!',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = 'Common Green Dragon Text' WHERE `creature_ai_texts`.`entry` = -404;
UPDATE `creature_ai_texts` SET `entry` = -405,`content_default` = '%s看起来冷静多了，现在正在吃$N的遗体。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 2,`language` = 0,`emote` = 0,`comment` = '2931' WHERE `creature_ai_texts`.`entry` = -405;
UPDATE `creature_ai_texts` SET `entry` = -406,`content_default` = '不要穿过黑铁矮人的地盘，$C。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '1169' WHERE `creature_ai_texts`.`entry` = -406;
UPDATE `creature_ai_texts` SET `entry` = -407,`content_default` = '我会放乾你的血，$C!',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '315' WHERE `creature_ai_texts`.`entry` = -407;
UPDATE `creature_ai_texts` SET `entry` = -408,`content_default` = '有人想要休息下吗?那么太可惜了!回去工作吧，笨蛋!',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '1763' WHERE `creature_ai_texts`.`entry` = -408;
UPDATE `creature_ai_texts` SET `entry` = -409,`content_default` = '火炮必须马上完工。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '1763' WHERE `creature_ai_texts`.`entry` = -409;
UPDATE `creature_ai_texts` SET `entry` = -410,`content_default` = '嗯，这一个看起来也就比风铃好一些。做好记录同学们……这不是在战斗激烈的时候你们想要召唤的东西。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '11582' WHERE `creature_ai_texts`.`entry` = -410;
UPDATE `creature_ai_texts` SET `entry` = -411,`content_default` = '今天的汉堡我会很高兴地在星期二那天付钱给你。',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 0,`type` = 0,`language` = 0,`emote` = 0,`comment` = '1395' WHERE `creature_ai_texts`.`entry` = -411;
